/// <reference path="../../../jspdf.d.ts" />
import { useEffect, useMemo } from "react";
import { useRecoilState } from 'recoil';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import api from "../../api";
import csv from '../../helpers/csv';
import apiEndpints from "../../config/apiEndpoints";
import { payrollDataState } from '../../store';
import { debounce } from 'lodash';

// Chat operation and save into store
const useCustomHook = () => {
  //get Payroll data from BE side
  const { PAYROLL_FINDALL } = apiEndpints;
  const [payrollData, setPayrollData] = useRecoilState(payrollDataState);
  const getData = async () => {
    const { data } = await api.get(PAYROLL_FINDALL, { page: 1, limit: 10 });
    setPayrollData(data)
  }
  useEffect(() => {
    getData()
  }, [])

  //search vehicle
  const changeHandler = async (e: any) => {
    const {data} = await api.get(PAYROLL_FINDALL, { page: 1, limit: 10, q: e });
    setPayrollData(data);
  }
  const debouncedResults = useMemo(() => {
    return debounce(changeHandler, 500);
  }, []);
  
  useEffect(() => {
    return () => {
      debouncedResults.cancel();
    };
  });

  //download pdf or excel functionality
  const downloadPdfOrCsv = (event: any, header: any, data: any, fileName: any) => {
    const type = event?.target?.innerText;

    if (type === "pdf" || type === "Pdf")
      pdf(`${fileName}`, header, data);
    else
      csv(`${fileName}`, header, data, true); // csv(fileName, header, data, hasAvatar)
  }


  const pdf = (fileName: string, header: any, data: any) => {
    const title = fileName;
    const unit = 'pt';
    const size = 'A4';
    const orientation = 'landscape';
    const marginLeft = 40;

    const body = data.map(({ no, name, department, joining_date, payroll_cycle }: any) =>
      [no, name, department, joining_date, payroll_cycle]
    );

    const doc = new jsPDF(orientation, unit, size);
    doc.setFontSize(15);
    doc.text(title, marginLeft, 40);

    doc.autoTable({
      head: [header],
      body: body,
      margin: { top: 50 },

      headStyles: {
        fillColor: [230, 244, 249],
        textColor: [20, 20, 42],
        fontStyle: 'normal',
        fontSize: 12,
      },

      didParseCell: async (item: any) => {
        if (item.row.section === "head")
          item.cell.styles.fillColor = [230, 244, 249];
        else
          item.cell.styles.fillColor = false;
      },

      didDrawCell: async (item: any) => {
        if (item.column.dataKey === 2 && item.section === "body") {
          const xPos = item.cell.x;
          const yPos = item.cell.y;
          var dim = 20;

          // const img = "data:image/jpeg;base64,";
          // doc.addImage(img, xPos+10, yPos, dim, dim);

          // doc.setFillColor(255, 0, 0);
          // doc.roundedRect(xPos,yPos+6, 100, 20, 5, 5, 'F'); //doc.roundedRect(xPos,yPos, width, height, radius, radius, 'F');

          // const img = new Image();
          // img.src = svg;
          // item.cell.padding('vertical', 0);
          // doc.addImage(img, 'PNG', xPos+10, yPos, 20, 20);
        }
      },
    });

    doc.save(`${fileName}.pdf`);
  };

  return {
    payrollData,
    changeHandler,
    downloadPdfOrCsv,
  };
};

export default useCustomHook;