/// <reference path="../../../../jspdf.d.ts" />

import jsPDF from 'jspdf';
import 'jspdf-autotable';
import api from '../../../api';
import csv from '../../../helpers/csv';
import svg from '../../assets/images/avatar1.png';


const useCustomHook = () => {

  // const [peronalChatList, setPeronalChatList] = useRecoilState(peronalChatListState);

  const getData = async (type: string): Promise<any> => {
    const { data } = await api.get(`${process.env.REACT_APP_APP_URL}/${type}`);
  };

  const downloadPdfOrCsv = (event: any, header: any, data: any, fileName: any, selectedTab: any) => {
    const type = event?.target?.innerText;

    if (type === "pdf" || type === "Pdf")
      pdf(`${fileName}`, header, data, selectedTab);
    else
      csv(`${fileName}`, header, data, true); // csv(fileName, header, data, hasAvatar)
  }

  const pdf = (fileName: string, header: any, data: any, selectedTab: any) => {
    const title = fileName;
    const unit = 'pt';
    const size = 'A4';
    const orientation = 'landscape';
    const marginLeft = 40;
    let TableData = () => {
      if (selectedTab === "1") {
        return data.map(({ no, subject, type, date, escalatedTo, status }: any) =>
          [no, subject, type, date, escalatedTo, status]
        )
      } else if (selectedTab === "2") {
        return data.map(({ no, subject, type, date, escalatedTo, status }: any) =>
          [no, subject, type, date, escalatedTo, status]
        )
      }
      else {
        null
      }
    }
    const body = TableData()
    const doc = new jsPDF(orientation, unit, size);
    doc.setFontSize(15);
    doc.text(title, marginLeft, 40);

    doc.autoTable({
      head: [header],
      body: body,
      margin: { top: 50 },

      headStyles: {
        fillColor: [230, 244, 249],
        textColor: [20, 20, 42],
        fontStyle: 'normal',
        fontSize: 12,
      },

      didParseCell: async (item: any) => {
        if (item.row.section === "head")
          item.cell.styles.fillColor = [230, 244, 249];
        else
          item.cell.styles.fillColor = false;
      },

      didDrawCell: async (item: any) => {
        if (item.column.dataKey === 1 && item.section === "body") {
          const xPos = item.cell.x;
          const yPos = item.cell.y;
          var dim = 20;

          // const img = "data:image/jpeg;base64,";
          // doc.addImage(img, xPos+10, yPos, dim, dim);

          // doc.setFillColor(255, 0, 0);
          // doc.roundedRect(xPos,yPos+6, 100, 20, 5, 5, 'F'); //doc.roundedRect(xPos,yPos, width, height, radius, radius, 'F');

          // const img = new Image();
          // img.src = svg;
          // item.cell.padding('vertical', 0);
          // doc.addImage(img, 'PNG', xPos+10, yPos, 20, 20);
        }
      },
    });

    doc.save(`${fileName}.pdf`);
  };

  return {
    getData,
    downloadPdfOrCsv
  };
};

export default useCustomHook;